mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT FAC_ID, FAC_NAME, FAC_ADDR, select_sub(FAC_ZIP2) AS region, FAC_ZIP2, FAC_ZIP, FAC_TYPE, (select file_Path from files where code='p02' and code_user=#{facId}) as filePath FROM FACILITY
AND FAC_ZIP2=#{facZip2}
AND FAC_TYPE = #{facType}
) A ) WHERE RN BETWEEN #{pageVO.start} AND #{pageVO.end}

	SELECT count(*) 
	  FROM FACILITY


            AND 	FAC_ZIP2=#{facZip2}


        	AND 	FAC_TYPE = #{facType}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT D1.DON_ID , D1.TITLE , D1.GOAL_AMT , D1.REC_STAT , FILE_PATH, SUM(NVL(D2.DON_AMT,0)) AS donationAmt, COUNT(DISTINCT D2.MEM_ID) AS donors, (SUM(D2.don_amt) / D1.goal_amt) * 100 AS donationRatio
FROM DONATION D1 LEFT JOIN DON_LEDGER D2 ON D1.DON_ID=D2.DON_ID
WHERE D1.REC_STAT ='0' AND D1.DON_REG_APP='1' AND D1.FAC_ID=#{facId}
GROUP BY D1.DON_ID,D1.TITLE, D1.GOAL_AMT, D1.REC_STAT
) A ) WHERE RN BETWEEN #{pageVO.start} AND #{pageVO.end}

SELECT    COUNT(*)
  FROM    DONATION
 WHERE    REC_STAT =#{recStat} 
   AND    DON_REG_APP='1'
   AND    FAC_ID=#{facId}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT D1.DON_ID, D1.TITLE , D1.GOAL_AMT , D1.REC_STAT , SUM(NVL(D2.DON_AMT,0)) AS donationAmt, COUNT(DISTINCT D2.MEM_ID) AS donors, (SUM(D2.don_amt) / D1.goal_amt) * 100 AS donationRatio
FROM DONATION D1 LEFT JOIN DON_LEDGER D2 ON D1.DON_ID=D2.DON_ID
WHERE D1.REC_STAT ='1' AND D1.DON_REG_APP='1' AND D1.FAC_ID=#{facId}
GROUP BY D1.DON_ID,D1.TITLE, D1.GOAL_AMT, D1.REC_STAT ) A ) WHERE RN BETWEEN #{pageVO.start} AND #{pageVO.end}

SELECT D.TITLE, D.REC_PERIOD, D.END_PERIOD, D.GOAL_AMT, D.DON_REG_APP, NVL(SUM(CASE WHEN DL.PAY_STAT ='g01' THEN DL.DON_AMT ELSE 0 END), 0) AS TARGETOK, NVL((SUM(DL.DON_AMT) / D.GOAL_AMT) * 100, 0) AS donationRatio, D.DON_ID, F.FAC_ID FROM DONATION D LEFT JOIN DON_LEDGER DL ON D.DON_ID = DL.DON_ID LEFT JOIN FACILITY F ON F.FAC_ID = D.FAC_ID
F.FAC_ID=#{facId}
AND REC_STAT=#{recStat}
GROUP BY D.TITLE, D.REC_PERIOD, D.END_PERIOD, D.GOAL_AMT, D.DON_REG_APP, D.DON_ID, F.FAC_ID

SELECT COUNT(*)
FROM VOL_MEM
WHERE VOL_ACT_ID=#{volActId}
AND MEM_ID=#{memId}
