mapper.select:
SELECT b.mem_id, b.mem_nick, c.file_path as mem_profile, sum(a.temp_change) as temp_change FROM HEART A LEFT JOIN MEMBER B ON A.MEM_ID = B.MEM_ID LEFT JOIN files C ON b.mem_id = c.code_user AND c.code = 'p01'
WHERE B.MEM_ID = #{memId}
GROUP BY b.mem_id, b.mem_nick, c.file_path

SELECT mem_id, board_id
FROM likes
WHERE mem_id = #{memId}
AND board_id = #{boardId}

mapper.delete:
DELETE
FROM likes
WHERE board_id = #{boardId}
AND mem_id = #{memId}
