mapper.select:
select file_path 
from files 
where code=#{code} and code_no=#{codeNo}

mapper.delete:
delete 
from files 
where code = #{code}
and code_no = #{codeNo}

mapper.insert:
insert into vol_mem(
	VOL_MEM_ID
	,VOL_ID
	,APP_CODE
	,APP_DATE
	,APP_REASON
	,MEM_ID
)
values(
	VOLMEMBER_SEQ.NEXTVAL
	,#{volId}
	,'h01'
	,sysdate
	,#{appReason}
	,#{memId}
)

mapper.select:
select count(*)
from vol_act
where vol_id = #{volId}

       	select count(*)
		from vol_review
		where vol_id = #{volId}

mapper.update:
update free_board
set title = #{title},
	content = #{content}
where VOL_ID = #{volId}
and BOARD_NO = #{boardNo}

mapper.delete:
delete 
from free_board
where VOL_ID = #{volId}
and BOARD_NO = #{boardNo}

mapper.select:
select count(*) 
from vol_mem 
where vol_id = #{volId}
and mem_id = #{memId}
and app_code = 'h02'

select m.vol_act_id,a.title
from vol_Act_mem m join vol_act a 
on m.VOL_ACT_ID=a.VOL_ACT_ID
where a.vol_id = #{volId}
and mem_id = #{memId}

select count(mem_id) cnt 
from vol_act_mem 
where vol_act_id=#{volActId}

mapper.update:
update vol_mem 
set app_code = 'h04' 
where vol_id=#{volId}
and mem_id=#{memId}
